import React, { useState, useCallback } from 'react';
import { Chess } from 'chess.js'; // Named export; ensure your version uses this format.
import Chessboard from './components/Chessboard';
import './index.css';

function App() {
  // Create a new Chess instance.
  const [game, setGame] = useState(new Chess());
  // Use the board's FEN string to represent the current position.
  const [position, setPosition] = useState(game.fen());
  // Message to display the game state (e.g., checkmate).
  const [message, setMessage] = useState('');

  // Handler for piece drops on the board.
  const handleDrop = useCallback((sourceSquare, targetSquare) => {
    // Attempt the move (always promoting to queen for simplicity).
    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q'
    });
    
    // If the move is illegal, cancel the drop.
    if (move === null) return false;

    // Update the board position.
    setPosition(game.fen());
    
    // Check if the game is over using the correct methods.
    if (game.isGameOver()) {
      if (game.isCheckmate()) {
        setMessage("Checkmate! Game over.");
      } else if (game.isDraw()) {
        setMessage("Draw! Game over.");
      } else {
        setMessage("Game over.");
      }
    } else {
      setMessage('');
    }
    
    return true;
  }, [game]);

  // Function to start a new game.
  const startNewGame = () => {
    const newGame = new Chess();
    setGame(newGame);
    setPosition(newGame.fen());
    setMessage('');
  };

  return (
    <div className="App">
      <h1>Simple Chess App</h1>
      <Chessboard position={position} onDrop={handleDrop} />
      {message && <p>{message}</p>}
      {game.isGameOver() && (
        <button onClick={startNewGame}>Start New Game</button>
      )}
    </div>
  );
}

export default App;
