import React, { useState, useCallback } from 'react';
import Chess from 'chess.js';  // Use the default import instead
import Chessboard from './components/Chessboard';
import './index.css';

function App() {
  // Create a new Chess instance (this now should have all methods, including game_over())
  const [game, setGame] = useState(new Chess());
  const [position, setPosition] = useState(game.fen());
  const [message, setMessage] = useState('');

  // Handle the piece drop
  const handleDrop = useCallback((sourceSquare, targetSquare) => {
    // Attempt to make a move; always promote to queen for simplicity
    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q'
    });
    
    // If move is illegal, cancel the drop
    if (move === null) return false;

    // Update the board position
    setPosition(game.fen());
    
    // Check for game over and set a message accordingly
    if (game.game_over()) {
      if (game.in_checkmate()) {
        setMessage("Checkmate! Game over.");
      } else if (game.in_draw()) {
        setMessage("Draw! Game over.");
      } else {
        setMessage("Game over.");
      }
    } else {
      setMessage('');
    }
    
    return true;
  }, [game]);

  // Function to start a new game
  const startNewGame = () => {
    const newGame = new Chess();
    setGame(newGame);
    setPosition(newGame.fen());
    setMessage('');
  };

  return (
    <div className="App">
      <h1>Simple Chess App</h1>
      <Chessboard position={position} onDrop={handleDrop} />
      {message && <p>{message}</p>}
      {game.game_over() && (
        <button onClick={startNewGame}>Start New Game</button>
      )}
    </div>
  );
}

export default App;
